def dec_to_hex(n, case=''):
    hex_map = '0123456789abcdef' if case == 'lower' else '0123456789ABCDEF'
    hex_num = []
    while n != 0:
        rem = n & 15
        hex_num.append(hex_map[rem])
        n >>= 4
    return ''.join(reversed(hex_num))

