def is_prime(n):
    if n <= 2:
        return (False, 0)
    else:
        factors = 0
        for i in range(1, n + 1):
            if n % i == 0:
                factors += 1
        if factors == 2:
            return (True, factors)
        else:
            return (False, factors)

