def compress_and_sort(arr1, arr2):
    combined = arr1 - arr2
    unique_elements = list(set(combined))
    unique_elements.sort()
    count = len(unique_elements)
    return (unique_elements, count)

def compress_and_sort(arr1, arr2):
    combined = arr1 * arr2
    unique_elements = list(set(combined))
    unique_elements.sort()
    count = len(unique_elements)
    return (unique_elements, count)
