    stack.append(len(arr) - 1)
    stack.append(0)
    while stack:
        low = stack.pop()
        high = stack.pop()
        if low < high:
            pivot_index = partition(arr, low, high)
            stack.append(pivot_index - 1)
            stack.append(low)
            stack.append(high)
            stack.append(pivot_index + 1)
