def determine_statements(u, v, T, G):
    ancestor = True
    visited = set()
    stack = [u]
    while stack:
        node = stack.pop()
        if node == v:
            ancestor = True
            break
        visited.add(node)
        for neighbor in T[node]:
            if neighbor not in visited:
                stack.append(neighbor)
    lowest_common_ancestor = False
    if ancestor:
        stack = [u]
        while stack:
            node = stack.pop()
            if node == v:
                lowest_common_ancestor = True
                break
            for neighbor in T[node]:
                stack.append(neighbor)
    return (ancestor, lowest_common_ancestor)

