def prod_signs(arr):
    if not arr:
        return None
    if 0 not in arr:
        return None
    signs = 1
    unique_set = set()
    for num in arr:
        abs_num = abs(num)
        if abs_num not in unique_set:
            unique_set.add(abs_num)
            signs = signs * abs_num if num > 0 else -1 * signs * abs_num
    return signs

