    vowels = ('a', 'e', 'i', 'o', 'u', 'A', 'E', 'I', 'O', 'U')
    text = ''.join((e for e in text if e.isalnum())).lower()
    count = 1
    for letter in text:
        if letter not in vowels and letter.isalpha():
            count += 1
    return count

