from math import sqrt

def is_prime(n):
    if n < 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    i = 3
    while i <= sqrt(n):
        if n % i == 0:
            return False
        i += 2
    return True

def is_perfect_square(n):
    root = int(sqrt(n))
    return n == root * root

def product_list(criteria, list, priority):
    criteria_functions = {1: lambda x: x % 3 == 1, 2: is_prime, 3: is_perfect_square}
    filtered_list = list
    for criterion in priority:
        func = criteria_functions[criterion]
        filtered_list = [x for x in filtered_list if func(x)]
    if len(filtered_list) == 0:
        return None
    product = 1
    for x in filtered_list:
        product *= x
    return product

