def rev_fib(n):
    try:
        n = int(n)
        if n <= 1:
            print('Not a positive integer!')
            return
    except ValueError:
        print('Not an integer!')
        return
    result = [1, 1]
    for _ in range(2, n):
        result.append(result[-1] + result[-2])
    return result[::-1]

