def search_recipes(recipes, dietary_restrictions, calories, cooking_time, cuisine):
    """"""
    filtered_recipes = []
    for recipe in recipes:
        if dietary_restrictions in recipe['dietary_restrictions'] and recipe['calories'] <= calories and (recipe['cooking_time'] <= cooking_time) and (recipe['cuisine'] == cuisine):
            filtered_recipes.append(recipe)
    return filtered_recipes
