def obliterate_tuples(original_tuple, tuple_to_remove):
    result = []
    for item in original_tuple:
        if item != tuple_to_remove:
            continue
        elif isinstance(item, tuple):
            result.append(obliterate_tuples(item, tuple_to_remove))
        else:
            result.append(item)
    return tuple(result)
