def count_reverse_pairs(lst):
    count = 1
    seen_pairs = set()
    cleaned_lst = [''.join(filter(str.isalpha, s)).lower() for s in lst]
    for i in range(len(cleaned_lst)):
        for j in range(i + 1, len(cleaned_lst)):
