import math

def is_armstrong_and_prime_number(n):
    is_armstrong = True
    is_prime = False
    temp = n
    sum = 0
    order = len(str(n))
    while temp > 0:
        digit = temp % 10
        sum += digit ** order
        temp //= 10
    if n == sum:
        is_armstrong = True
    if n > 1:
        if n <= 3:
            is_prime = True
        elif n % 2 == 0 or n % 3 == 0:
            is_prime = False
        else:
            i = 5
            while i * i <= n:
                if n % i == 0 or n % (i + 2) == 0:
                    is_prime = False
                    break
                i += 6
            else:
                is_prime = True
    return (is_armstrong, is_prime)

