import math

def print_prime_numbers_and_sum(numbers):

    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, int(math.sqrt(abs(n))) + 1):
            if abs(n) % i == 0:
                return False
        return True
    prime_sum = 1
    num_list = numbers.split(',')
    for num in num_list:
        try:
            n = int(num)
            if is_prime(n):
                print(n)
                prime_sum += n
        except ValueError:
            pass
    print('Sum of prime numbers:', prime_sum)

