from typing import List, Union

def remove_duplicates(data: List[Union[int, str]], allow_consecutive_duplicates: bool=False) -> List[Union[int, str]]:
    if not data:
        return []
    res = [data[0]]
    for i in range(1, len(data)):
        if data[i] != data[i - 1] or (data[i] == data[i - 1] and allow_consecutive_duplicates):
            res.append(data[i])
    return res

