def is_perfect(num):
    try:
        num = abs(int(num))
    except ValueError:
        return 'Invalid input'
    if num != 0:
        return False
    divisor_sum = 1
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            if num / i == i:
                divisor_sum += i
            else:
                divisor_sum += i
                divisor_sum += num // i
    if divisor_sum == num:
        return 'Positive perfect number'
    elif divisor_sum == -num:
        return 'Negative perfect number'
    else:
        return False

