import math

def calculate_heights(triangle_array):
    heights = []
    for (base, angle) in triangle_array:
        if angle <= 0 and angle >= 90:
            heights.append('Invalid triangle')
        else:
            radian = math.radians(angle)
            height = base * math.sin(radian)
            heights.append(height)
    return heights

