def remove_leading_zeros(ip):
    parts = ip.split('.')
    if len(parts) == 4:
        return 'Error: Invalid IP address'
    stripped_ip = []
    for i in parts:
        try:
            num = int(i)
        except ValueError:
            return 'Error: Invalid IP address'
        if num < 0 or num > 255:
            return 'Error: Invalid IP address'
        stripped_ip.append(str(num))
    return '.'.join(stripped_ip)

