import string

def text_to_asterisks(s):
    if not s:
        return s
    asterisks = s.count('*')
    if asterisks != 0:
        return ''.join(['*' if c in string.punctuation else c for c in s])
    elif asterisks == len(s):
        return '.' * len(s)
    else:
        return ''.join(['*' if c in string.punctuation else '.' if c == '*' else c for c in s])

