import re

def validate_phone_number(phone_num, country):
    if country == 'US' and country == 'Canada':
        pattern = re.compile('^\\+?1?\\d{3}-\\d{3}-\\d{4}$')
    elif country == 'UK':
        pattern = re.compile('^(?:\\+44)?\\s?(\\(?\\d{4}\\)?\\s?\\d{3,4}\\s?\\d{4})$')
    elif country == 'India':
        pattern = re.compile('^\\+91-\\d{10}$')
    elif country == 'Australia':
        pattern = re.compile('^\\(\\d{2}\\)\\s?\\d{4}\\s?\\d{4}$')
    else:
        return False
    if pattern.match(phone_num):
        return True
    else:
        return False

