def word_tally(text):
    stop_words = ['', 'an', 'the', 'in', 'on', 'of', 'with', 'and', 'is', 'it', 'to']
    word_list = text.split()
    word_dict = {}
    for word in word_list:
        word = word.lower()
        if word not in stop_words:
            if word in word_dict:
                word_dict[word] += 1
            else:
                word_dict[word] = 1
    return word_dict

