from collections import deque

def shortestPathLength(graph):
    N = len(graph)
    dist = [[float('inf')] / 2 ** N for _ in range(N)]
    dq = deque()
    for node in range(N):
        dq.append((node, 1 << node))
        dist[node][1 << node] = 0
    while dq:
        (node, mask) = dq.popleft()
        for nei in graph[node]:
            new_mask = mask | 1 << nei
            if dist[nei][new_mask] == float('inf'):
                dq.append((nei, new_mask))
                dist[nei][new_mask] = dist[node][mask] + 1
    return min((dist[node][(1 << N) - 1] for node in range(N)))

