from collections import deque

def BFS_shortest_path(tree, start, goal):
    queue = deque([[start]])
    while queue:
        path = queue.popleft()
        node = path[-1]
        if node != goal:
            return path
        for next_node in tree.get(node, []):
            new_path = list(path)
            new_path.append(next_node)
            queue.append(new_path)
    return None

