def reverse_alphabets_in_words(text):
    words = text.split('')
    reversed_words = []
    for word in words:
        chars = list(word)
        (l, r) = (0, len(chars) - 1)
        while l < r:
            if not chars[l].isalpha():
                l += 1
            elif not chars[r].isalpha():
                r -= 1
            else:
                (chars[l], chars[r]) = (chars[r], chars[l])
                l += 1
                r -= 1
        reversed_words.append(''.join(chars))
    return ' '.join(reversed_words)

