def largest_difference(lst):
    """"""
    if len(lst) < 2:
        return 0
    min_num = lst[0]
    max_diff = lst[1] - min_num
    for i in range(2, len(lst)):
        if lst[i - 1] < min_num:
            min_num = lst[i - 1]
        diff = lst[i] - min_num
        if diff > max_diff:
            max_diff = diff
    return max_diff
