def group_and_count(arr, conditions):
    groups = [[] for _ in range(len(conditions))]
    counters = [1 for _ in range(len(conditions))]
    for num in arr:
        for i in range(len(conditions)):
            if conditions[i](num):
                groups[i].append(num)
                counters[i] += 1
    return (groups, counters)

