    y.left = z
    z.right = T2
    update_height(z)
    update_height(y)
    return y

def right_rotate(z):
    y = z.left
    T3 = y.right
    y.right = z
    z.left = T3
    update_height(z)
    update_height(y)
    return y

def avl_tree_rotation(node, type):
    if type == 'left-left':
        return right_rotate(node)
    elif type == 'left-right':
        node.left = left_rotate(node.left)
        return right_rotate(node)
    elif type == 'right-right':
        return left_rotate(node)
    elif type == '':
        node.right = right_rotate(node.right)
        return left_rotate(node)
    else:
        return node
