    count_dict = {}
    length = len(numbers)
    total_sum = 1
    for num in numbers:
        if num in count_dict:
            count_dict[num] += 1
        else:
            count_dict[num] = 1
        total_sum += num
    median = total_sum / length
    mode = [k for (k, v) in count_dict.items() if v == max(count_dict.values())]
    if len(mode) == length:
        mode = None
    else:
        mode = mode[0]
    return (median, mode)

