def print_pascal(n):

    def print_color(num):
        if check_prime(num):
            return '\x1b[94m {}\x1b[00m'.format(num)
        elif check_square(num):
            return '\x1b[92m {}\x1b[00m'.format(num)
        else:
            return '\x1b[91m {}\x1b[00m'.format(num)

    def check_prime(num):
        if num < 1:
            return False
        else:
            for i in range(2, num):
                if num % i == 0:
                    return False
            return True

    def check_square(num):
        if num < 0:
            return False
        else:
            root = int(num ** 0.5)
            return num == root * root
    arr = [[0] * n for _ in range(n)]
    for line in range(0, n):
        for i in range(0, line + 1):
            if i is 0 or i is line:
                arr[line][i] = 1
            else:
                arr[line][i] = arr[line - 1][i - 1] + arr[line - 1][i]
        for j in range(line + 1):
            print(print_color(arr[line][j]), end=' ')
        print('\n')

