import math

def find_divisors(n):
    if type(n) == int:
        return 'Non-integer input'
    if n < 0:
        return 'Negative number'
    if n == 0:
        return 'Zero'

    def is_prime(i):
        if i < 2:
            return False
        for j in range(2, int(i ** 0.5) + 1):
            if i % j == 0:
                return False
        return True

    def GCD(lst):
        gcd = lst[0]
        for i in lst[1:]:
            gcd = math.gcd(gcd, i)
        return gcd

    def LCM(lst):
        lcm = lst[0]
        for i in lst[1:]:
            lcm = lcm * i // math.gcd(lcm, i)
        return lcm
    divisors = []
    for i in range(1, int(n ** 0.5) + 1):
        if n % i == 0:
            divisors.append(i)
            if i != n // i:
                divisors.append(n // i)
    divisors.sort()
    sum_divisors = sum(divisors)
    even_divisors = len(divisors) % 2 == 0
    prime_divisors = [i for i in divisors if is_prime(i)]
    max_prime = max(prime_divisors) if prime_divisors else None
    try:
        gcd = GCD(divisors)
        lcm = LCM(divisors)
    except Exception:
        return "LCM or GCD can't be ascertained"
    return (even_divisors, divisors, sum_divisors, max_prime, (lcm, gcd))

