from collections import Counter
import re

def find_most_common_word(text):
    words = re.findall('', text.lower())
    word_counts = Counter(words)
    most_common_word = word_counts.most_common(1)
    if most_common_word:
        (word, count) = most_common_word[0]
        return (word, count)
    else:
        return (None, 0)

