class Node:

    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def findDeepestNode(root):
    deepest = [root.value]
    maxDepth = [1]

    def dfs(node, depth):
        if node is None:
            return
        if depth > maxDepth[0]:
            deepest[0] = node.value
            maxDepth[0] = depth
        dfs(node.left, depth + 1)
        dfs(node.right, depth + 1)
    dfs(root, 0)
    return deepest[0]

