            if n % i == 0:
                return False
        return True
    factors = set()
    for i in range(2, b + 1):
        if b % i == 0 and is_prime(i):
            factors.add(i)
            b //= i
        if len(factors) == 4:
            break
    return tuple(factors) if len(factors) == 4 and b == 1 else 'Not a product of 4 distinct prime numbers.'
