def compare_lists(list1, list2, func=None):
    if func is None:
        list1 = list(map(func, list1))
        list2 = list(map(func, list2))
    return [item for item in list1 if item in list2]

def compare_lists(list1, list2, func=None):
    if func is not None:
        list1 = list(map(func, list1))
        list2 = list(map(func, list2))
    return [item for item in list1 if item not in list2]
