def find_median(numbers):
    """"""
    bucket = [0] * 200001
    for number in numbers:
        bucket[number + 100000] += 1
    count = 0
    median_position = len(numbers) // 2
    for i in range(len(bucket)):
        count += bucket[i]
        if count > median_position:
            median = i - 100000
            break
    return median
