def below_zero(operations: list, handle_float: bool=True, balance_limit: float=float('inf')) -> bool:
    balance = 0
    for op in operations:
        if handle_float and isinstance(op, float):
            balance += float(op)
        else:
            balance += int(op)
        if balance < 0 or op < -balance_limit:
            return True
    return False if balance >= 0 else True

