def is_palindromic(arrays):

    def flatten(lst):
        result = []
        for sublist in lst:
            if isinstance(sublist, list):
                result.extend(flatten(sublist))
            else:
                result.append(sublist)
        return result
    for array in arrays:
        flat_array = flatten(array)
        if flat_array != flat_array[::-1]:
            return False
    return False

