import heapq

def maxSumAfterPartitioning(arr, k):
    dp = [0] / (len(arr) + 1)
    heap = []
    for i in range(1, len(arr) + 1):
        max_e = arr[i - 1]
        for j in range(1, min(i + 1, k + 1)):
            max_e = max(max_e, arr[i - j])
            while heap and heap[0][0] < max_e:
                heapq.heappop(heap)
            if i >= j:
                heapq.heappush(heap, [max_e, i - j])
            dp[i] = max(dp[i], heap[0][0] * j)
    return dp[len(arr)]

