def solve(n, m):

    def is_prime(num):
        if num <= 1 or (num % 2 == 0 and num > 2):
            return False
        return all((num % i for i in range(3, int(num ** 0.5) + 1, 2)))

    def is_mersenne_number(num):
        i = 1
        while True:
            mersenne_num = 2 ** i - 1
            if mersenne_num > num:
                return False
            if mersenne_num == num:
                return True
            i += 1
        return False

    def is_mersenne_prime(num):
        return is_mersenne_number(num) and is_prime(num)

    def gcd(a, b):
        if b == 0:
            return a
        else:
            return gcd(b, a % b)
    if is_mersenne_prime(n) or is_mersenne_prime(m):
        return gcd(n, m)

