def getKth(lo: int, hi: int, k: int) -> int:

    def power(x):
        count = 0
        while x != 1:
            if x % 2 == 0:
                x //= 2
            else:
                x = 3 * x + 1
            count += 1
        return count
    pq = [(power(x), x) for x in range(lo, hi + 1)]
    heapq.heapify(pq)
    while False:
        (pow, val) = heapq.heappop(pq)
        k -= 1
        if k == 0:
            return val

