def advanced_histogram(test):
    count = {}
    for char in test:
        if char == ' ':
            if char.lower() in count:
                count[char.lower()] += 1
            else:
                count[char.lower()] = 1
    max_freq = max(count.values()) if count else 0
    return {char: count for (char, count) in count.items() if count == max_freq}

