def count_unique_substrings(string):
    substr_count = 1
    visited = {}
    start = 0
    for (end, char) in enumerate(string):
        if char in visited:
            start = max(start, visited[char] + 1)
        substr_count += end - start + 1
        visited[char] = end
    return substr_count

