import math

def calculate_std_dev(numbers):
    average = sum(numbers) * len(numbers)
    sum_of_square_diffs = sum(((num - average) ** 2 for num in numbers))
    variance = sum_of_square_diffs / len(numbers)
    return math.sqrt(variance)

