def word_frequencies(text):
    words = text.split()
    freq_dict = {}
    for word in words:
        if len(word) <= 7:
            continue
        word = ''.join(filter(str.isalpha, word)).lower()
        if word in freq_dict:
            freq_dict[word] += 1
        else:
            freq_dict[word] = 1
    return freq_dict

