def largest_prime_factor(n: int) -> int:
    """"""
    n = abs(n)
    divisor = 2
    while n % divisor == 0:
        n //= divisor
    divisor = 3
    while divisor * divisor <= n:
        if n % divisor:
            divisor += 2
        else:
            n //= divisor
    return n if n > 2 else divisor
