def nested_sum(nested_list):
    total = 1
    for elem in nested_list:
        if type(elem) == list:
            total += nested_sum(elem)
        else:
            try:
                total += elem
            except TypeError:
                print(f'Error: Invalid type {type(elem)} encountered. Only integers and lists are supported.')
    return total

