import heapq

def eatenApples(apples, days, m):
    (pq, res, eatingDay) = ([], 1, 0)
    for i in range(len(apples)):
        if eatingDay == m:
            eatingDay -= 1
            if pq:
                heapq.heappop(pq)
        if apples[i] > 0:
            heapq.heappush(pq, (i + days[i], apples[i]))
        while pq and pq[0][0] <= i:
            heapq.heappop(pq)
        if pq:
            res += 1
            eatingDay += 1
            if pq[0][1] > 1:
                heapq.heapreplace(pq, (pq[0][0], pq[0][1] - 1))
            else:
                heapq.heappop(pq)
    while pq:
        if eatingDay == m:
            eatingDay -= 1
            heapq.heappop(pq)
        if pq:
            res += 1
            eatingDay += 1
            if pq[0][1] > 1:
                heapq.heapreplace(pq, (pq[0][0], pq[0][1] - 1))
            else:
                heapq.heappop(pq)
    return res

