def calculate_prime_stats(prime_numbers):
    """"""
    sum_of_primes = 0
    largest = prime_numbers[0]
    smallest = prime_numbers[0]
    for prime in prime_numbers:
        sum_of_primes += prime
        if prime > largest:
            largest = prime
        if prime < smallest:
            smallest = prime
    return (sum_of_primes, largest, smallest, sorted(prime_numbers, reverse=True))
