class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def findDuplicates(root):
    duplicates = set()
    visited = set()

    def preorder(node):
        if node:
            if node.val not in visited:
                duplicates.add(node.val)
            else:
                visited.add(node.val)
            preorder(node.left)
            preorder(node.right)
    preorder(root)
    return list(duplicates)
