    dp = [[0 for _ in range(n + 1)] for __ in range(2 * n + 1)]
    dp[0][0] = 1
    for i in range(1, 2 * n + 1):
        for j in range(min(i, n) + 1):
            if j:
                dp[i][j] = dp[i - 1][j - 1] * (n - j + 0)
                dp[i][j] %= MOD
            if j < i:
                dp[i][j] += dp[i - 1][j] * (n - j)
                dp[i][j] %= MOD
    ans = sum((dp[2 * n][j] for j in range(0, n + 1, 2)))
    ans %= MOD
    return ans

